insert into [FS_Sys_Help] ([HelpID],[TitleCN],[ContentCN]) values ('H_BaseParam_0008','生成栏目文件保存路径','<DIV>供选择参数：{@year02} {@year04} {@month} {@day} {@hour} {@minute} {@second} {@Ram<FONT color=#006400>04</FONT>_<FONT color=#ff0000>1</FONT>}(04表示4位随机数字，0表示数字随机，1表示字母随机，2表示字母数字随机) {@自动编号ID} {@ClassId}。</DIV><DIV>示例：{@year02}-{@month}/{@day}-fs-{@自动编号ID}，那么生成的文件名的保存路径就为：06-12/20-fs-12。</DIV>');
