insert into [FS_Sys_Help] ([HelpID],[TitleCN],[ContentCN]) values ('H_UserParam_0001','会员注册默认会员组','会员注册的时候默认会员组。会员组可以在会员组管理中进行管理。会员组必须要建立一个默认的。以方便用户注册的时候默认使用。');
